var StartAnimation = [];
var maxWidth = $(".img-member-lg-0.img-member-0").width();
var n = 0;
var screenHeight = 0;
var MidScreen = 0;
var outerWidth = 0;
var size = 0;

	$(window).scroll(function(){

		if($(window).width() >= 992){ //apenas dispositivos onde são 3 membros por linha (width >= 992px)

			n = 2; //numero de "linhas" de membros
			for(var i=0; i<n; i++)
				StartAnimation[i] = $(".img-member-lg-" + i).offset().top + $(".img-member-lg-" + i).height()/2 - maxWidth; 

			for(var i=0; i<n; i++){

				screenHeight = $(window).height();
				MidScreen = $(window).scrollTop() + screenHeight/2;
				outerWidth = $(".img-member-lg-" + 0).outerWidth;

				if( MidScreen >= StartAnimation[i]){
					
					size = (MidScreen - StartAnimation[i]);
					if(size >= maxWidth) size = maxWidth;
					if(size < maxWidth/2) size = maxWidth/2;

					$(".img-member-lg-" + i).css({
						"width": size + "px",
						"padding": (maxWidth - size)/2 + "px" + " 0px"
					});
				} else {
					size = maxWidth/2;

					$(".img-member-lg-" + i).css({
						"width": size +"px",
						"padding": (maxWidth - size)/2 + "px" + " 0px"
					});
				}
			}
		} else {

			n = 3; //numero de "linhas" de membros
			for(var i=0; i<n; i++)
				StartAnimation[i] = $(".img-member-" + i).offset().top + $(".img-member-" + i).height()/2 - maxWidth;
		
			for(var i=0; i<n; i++){

				screenHeight = $(window).height();
				MidScreen = $(window).scrollTop() + screenHeight/2;
				outerWidth = $(".img-member-" + 0).outerWidth;

				if( MidScreen >= StartAnimation[i]){
					
					size = (MidScreen - StartAnimation[i]);
					if(size >= maxWidth) size = maxWidth;
					if(size < maxWidth/2) size = maxWidth/2;

					$(".img-member-" + i).css({
						"width": size + "px",
						"padding": (maxWidth - size)/2 + "px" + " 0px"
					});
				} else {
					size = maxWidth/2;

					$(".img-member-" + i).css({
						"width": size +"px",
						"padding": (maxWidth - size)/2 + "px" + " 0px"
					});
				}
			}	

		}
	})
